import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { NgToastService } from 'ng-angular-popup';
import ValidateForm from 'src/app/helpers/validateform';
import { AuthService } from 'src/app/services/auth.service';

@Component({
  selector: 'app-signup-vendor',
  templateUrl: './signup-vendor.component.html',
  styleUrls: ['./signup-vendor.component.css']
})
export class SignupVendorComponent {

  type: string = "password";
  isText: boolean = false;
  eyeIcon: string = "fa-eye-slash";
  signUpForm!: FormGroup;

  constructor(
    private fb: FormBuilder, 
    private auth: AuthService, 
    private router: Router,
    private toast: NgToastService
  ) {}

  ngOnInit(): void{
      this.signUpForm = this.fb.group({
         firstName : ['',Validators.required],
         lastName: ['',Validators.required],
         email : ['',Validators.required],
         userName : ['',Validators.required],
         password : ['',Validators.required],
         mobileNumber : ['',Validators.required],
         pan : ['',Validators.required],
         gst : ['',Validators.required],
         accountNumber : ['',Validators.required]
      }) 
  }

  hideShowPassword(){
    this.isText = !this.isText;
    this.isText ? this.eyeIcon = "fa-eye" : this.eyeIcon = "fa-eye-slash";
    this.isText ? this.type = "text" : this.type = "password";
  }

  onSignup(){
    if(this.signUpForm.valid){

      //push data into database
      // console.log(this.signUpForm.value);
      this.auth.signUpVendor(this.signUpForm.value)
      .subscribe({
        next: (res=>{
          // alert(res.message);
          this.toast.success({detail:"SUCCESS", summary:res.message, duration: 5000});
          this.signUpForm.reset();
          this.router.navigate(['login']);
        }),
        error:(err=>{
          // console.log('Hello world' + err);
          alert(err?.error.message);
          // this.toast.error({detail:"ERROR", summary:"Something wrong", duration: 5000});
        })
      })
    }
    else{

      ValidateForm.validateAllFormFields(this.signUpForm);
        //logic for throwing errors

    }
  }
}
<div class="outer">
    <div class="container">
        <div class="row">
            <div class="offset-md-2 col-lg-5 col-md-7 offset-lg-4 offset-md-3">
                <div class="panel border bg-white">
                    <div class="panel-heading">
                        <h3 class="pt-3 font-weight-bold">Vendor Sign Up</h3>
                    </div>
                    <div class="panel-body p-3">
                        <form [formGroup]="signUpForm">
                            <div class="form-group pt-3">
                                <div class="input-field" [class.error]="signUpForm.controls['firstName'].dirty && signUpForm.hasError('required','firstName')">
                                    <span class="fa fa-user p-2"></span>
                                    <input formControlName="firstName" type="text" placeholder="First Name">
                                </div>
                            </div>
                            <small *ngIf="signUpForm.controls['firstName'].dirty && signUpForm.hasError('required','firstName')" class="text-danger">FirstName is required</small>
                            <div class="form-group pt-3">
                                <div class="input-field" [class.error]="signUpForm.controls['lastName'].dirty && signUpForm.hasError('required','lastName')">
                                    <span class="fa fa-user p-2"></span>
                                    <input formControlName="lastName" type="text" placeholder="Last Name">
                                </div>
                            </div>
                            <small *ngIf="signUpForm.controls['lastName'].dirty && signUpForm.hasError('required','lastName')" class="text-danger">LastName is required</small>
                            <div class="form-group pt-3">
                                <div class="input-field" [class.error]="signUpForm.controls['email'].dirty && signUpForm.hasError('required','email')">
                                    <span class="fa fa-envelope p-2"></span>
                                    <input formControlName="email" type="text" placeholder="Email">
                                </div>
                            </div>
                            <small *ngIf="signUpForm.controls['email'].dirty && signUpForm.hasError('required','email')" class="text-danger">Email is required</small>
                            <div class="form-group pt-3">
                                <div class="input-field" [class.error]="signUpForm.controls['userName'].dirty && signUpForm.hasError('required','userName')">
                                    <span class="fa fa-user p-2"></span>
                                    <input formControlName="userName" type="text" placeholder="Username">
                                </div>
                            </div>
                            <small *ngIf="signUpForm.controls['userName'].dirty && signUpForm.hasError('required','userName')" class="text-danger">UserName is required</small>
                            <div class="form-group pt-3">
                                <div class="input-field" [class.error]="signUpForm.controls['password'].dirty && signUpForm.hasError('required','password')">
                                    <span class="fa fa-lock p-2"></span>
                                    <input formControlName="password" [type]="type" placeholder="Password">
                                    <span class="fa {{ eyeIcon }}" (click)="hideShowPassword()"></span>
                                </div>
                            </div>
                            <small *ngIf="signUpForm.controls['password'].dirty && signUpForm.hasError('required','password')" class="text-danger">Password is required</small>
                            <div class="form-group pt-3">
                                <div class="input-field" [class.error]="signUpForm.controls['mobileNumber'].dirty && signUpForm.hasError('required','mobileNumber')">
                                    <span class="fa fa-mobile p-2"></span>
                                    <input formControlName="mobileNumber" type="text" placeholder="Mobile Number">
                                </div>
                            </div>
                            <small *ngIf="signUpForm.controls['mobileNumber'].dirty && signUpForm.hasError('required','mobileNumber')" class="text-danger">Mobile Number is required</small>

                            <div class="form-group pt-3">
                                <div class="input-field" [class.error]="signUpForm.controls['pan'].dirty && signUpForm.hasError('required','pan')">
                                    <span class="fa fa-mobile p-2"></span>
                                    <input formControlName="pan" type="text" placeholder="PAN Number">
                                </div>
                            </div>
                            <small *ngIf="signUpForm.controls['pan'].dirty && signUpForm.hasError('required','pan')" class="text-danger">PAN Number is required</small>
                            <div class="form-group pt-3">
                                <div class="input-field" [class.error]="signUpForm.controls['gst'].dirty && signUpForm.hasError('required','mobileNumber')">
                                    <span class="fa fa-mobile p-2"></span>
                                    <input formControlName="mobileNumber" type="text" placeholder="Mobile Number">
                                </div>
                            </div>
                            <small *ngIf="signUpForm.controls['mobileNumber'].dirty && signUpForm.hasError('required','mobileNumber')" class="text-danger">Mobile Number is required</small>
                            <div class="form-group pt-3">
                                <div class="input-field" [class.error]="signUpForm.controls['mobileNumber'].dirty && signUpForm.hasError('required','mobileNumber')">
                                    <span class="fa fa-mobile p-2"></span>
                                    <input formControlName="mobileNumber" type="text" placeholder="Mobile Number">
                                </div>
                            </div>
                            <small *ngIf="signUpForm.controls['mobileNumber'].dirty && signUpForm.hasError('required','mobileNumber')" class="text-danger">Mobile Number is required</small>

                            <button 
                                type="submit" 
                                class="btn btn-primary btn-block mt-4 w-100"
                                (click)="onSignup()">Sign Up</button>
                            <div class="text-center pt-4 text-muted">Already have account? <a id="forget" routerLink="/login">Login</a></div>
                        </form>
                    </div>
                    <!-- <div class="mx-3 my-2 py-2 bordert">
                        <div class="text-center py-3">
                            <a href="https://www.facebook.com" target="_blank" class="px-2"><img src="https://www.freepnglogos.com/uploads/aqua-blue-f-facebook-logo-png-22.png" alt="facebook"></a>
                            <a href="https://www.google.com" target="_blank" class="px-2"><img src="https://www.freepnglogos.com/uploads/google-favicon-logo-20.png" alt="google"></a>
                            <a href="https://www.outlook.com" target="_blank" class="px-2"><img src="https://www.freepnglogos.com/uploads/logo-outlook/outlook-transparent-save-outlook-png-icons-and-png-backgrounds-0.png" alt="outlook"></a>
                        </div>
                    </div> -->
                </div>
            </div>
        </div>
    </div>
</div>
