import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ResetPassword } from 'src/app/models/reset-password.model';
import { ReactiveFormsModule } from '@angular/forms';
import { ConfirmPasswordValidator } from 'src/app/helpers/confirm-password.validator';
import { ActivatedRoute } from '@angular/router';
import ValidateForm from 'src/app/helpers/validateform';
import { ResetPasswordService } from 'src/app/services/reset-password.service';

@Component({
  selector: 'app-reset',
  templateUrl: './reset.component.html',
  styleUrls: ['./reset.component.css']
})
export class ResetComponent implements OnInit {
    resetPasswordForm!: FormGroup;
    emailToReset!: string;
    emailToken!: string;
    reserPasswordObj = new ResetPassword();

    constructor(private fb: FormBuilder,
      private activatedRoute : ActivatedRoute,
      private resetService: ResetPasswordService
      ) {

    }

    ngOnInit(): void {
      this.resetPasswordForm = this.fb.group({
        password: [null, Validators.required],
        confirmPassword:[null,Validators.required]
      },{
        validator: ConfirmPasswordValidator("password","confirmPassword")
      });

      this.activatedRoute.queryParams.subscribe(val =>{
        this.emailToReset = val['email'];
        let uriToken = val['code'];
        this.emailToken = uriToken.replace(/ /g,'+');
      })
    }

    reset(){
      if(this.resetPasswordForm.valid){
        this.reserPasswordObj.email = this.emailToReset;
        this.reserPasswordObj.newPassword = this.resetPasswordForm.value.password;
        this.reserPasswordObj.confirmPassword = this.resetPasswordForm.value.confirmPassword;
        this.reserPasswordObj.emailToken = this.emailToken;
        this.resetService.resetPassword(this.reserPasswordObj)
        .subscribe({
          next:(res)=>{}
        })

      }
      else{
        ValidateForm.validateAllFormFields(this.resetPasswordForm);
      }
    }
}
<div class="outer">
    <div class="container">
        <div class="row">
            <div class="offset-md-2 col-lg-5 col-md-7 offset-lg-4 offset-md-3">
                <div class="panel border bg-white">
                    <div class="panel-heading">
                        <h3 class="pt-3 font-weight-bold">Reset Password</h3>
                    </div>
                    <div class="panel-body p-3">
                        <form  [formGroup]="resetPasswordForm" (ngSubmit)="reset()">
                            <div class="form-group pt-3">
                                <div class="input-field" [class.error]="resetPasswordForm.controls['password'].dirty && resetPasswordForm.hasError('required','password')"> <span class="fa fa-lock px-2"></span> <input type="password" placeholder="New Password" formControlName="password">
                                </div>
                            </div>
                            <small *ngIf="resetPasswordForm.controls['password'].dirty && resetPasswordForm.hasError('required','password')" class="text-danger">*Password is required*</small>
                            <div class="form-group pt-3">
                                <div class="input-field" [class.error]="resetPasswordForm.controls['confirmPassword'].dirty && resetPasswordForm.hasError('required','confirmPassword')"> <span class="fa fa-lock px-2"></span> <input type="password" placeholder="Confirm Password " formControlName="confirmPassword">
                                </div>
                            </div>
                            <small *ngIf="resetPasswordForm.controls['confirmPassword'].dirty && resetPasswordForm.hasError('required','confirmPassword')" class="text-danger">*ConfirmPassword is required*</small>
                            <small *ngIf="resetPasswordForm.hasError('confirmPasswordValidator','confirmPassword')" class="text-danger">*Password didn't matched*</small>

                            <button type="submit" class="btn btn-primary btn-block mt-4 w-100">Reset</button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    </div>
</div>
