import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { AuthService } from 'src/app/services/auth.service';
import { EcommServiceService } from 'src/app/services/ecomm-service.service';
import { cartPost } from '../header/Cart.module';
import { NotificationService } from 'src/app/services/notification.service';

@Component({
  selector: 'app-cart',
  templateUrl: './cart.component.html',
  styleUrls: ['./cart.component.css']
})
export class CartComponent {
  mycart:cartPost;
  TotalCost=0;
  TotalQuantity=0;
  mycode:string="";
  Address:string="";
  idw: any ="";
  constructor(private my_service:EcommServiceService, 
    private router: Router,
    private noti : NotificationService) {
    this.mycart= new cartPost();

  }

  ngOnInit(){
    this.mycart = this.my_service.mycart;
    console.log("Cartttttt");
    console.log(this.mycart);


    for(let cost of this.mycart.items){
      this.TotalCost+=(cost.price*cost.quantity);

      this.TotalQuantity+=cost.quantity;
    }
  }

  applyCode(){
    if(this.mycode=='Get10'){
      this.TotalCost-=0.1*this.TotalCost;
    }
    else if(this.mycode=='Get20'){
      this.TotalCost-=0.2*this.TotalCost;
    }
    else if(this.mycode=='Get30'){
      this.TotalCost-=0.3*this.TotalCost;
    }
    else if(this.mycode=='Get40'){
      this.TotalCost-=0.4*this.TotalCost;
    }
    else if(this.mycode=='Get50'){
      this.TotalCost-=0.5*this.TotalCost;
    }
    else{
      this.TotalCost
    }
  }
  orderPlaced(){
    console.log("--------------------------------");
    console.log(this.TotalCost, this.TotalQuantity);
    this.my_service.placeOrder(this.TotalCost,this.TotalQuantity, this.Address);
    this.router.navigate(['/order']);
    this.idw = localStorage.getItem('userid');
    this.noti.sendEmailToUser(this.idw);

  }

}
<section class="h-100 h-custom" style="background-color:whitesmoke;">
  <div class="container py-5 h-100">
    <div class="row d-flex justify-content-center align-items-center h-100">
      <div class="col-12">
        <div class="card card-registration card-registration-2" style="border-radius: 15px;">
          <div class="card-body p-0">
            <div class="row g-0">
              <div class="col-lg-8">
                <div class="p-5">
                  <div class="d-flex justify-content-between align-items-center mb-5">
                    <h1 class="fw-bold mb-0 text-black">Shopping Cart</h1>
                    <h6 class="mb-0 text-muted">Your Buyer ID :- {{mycart.buyerId}}</h6>
                  </div>
                  <hr class="my-4">

                  <div class="row mb-4 d-flex justify-content-between align-items-center" *ngFor="let cartItems of mycart.items">
                    <div class="col-md-2 col-lg-2 col-xl-2">
                      <img
                        src="../../assets/images/{{cartItems.image}}"
                        class="img-fluid rounded-3" alt="Cotton T-shirt">
                    </div>
                    <div class="col-md-3 col-lg-3 col-xl-3">
                      <h6 class="text-muted">{{cartItems.productName}}</h6>
                      <h5 class="text-black mb-0">{{cartItems.productName}}</h5>
                    </div>
                    <div class="col-md-3 col-lg-3 col-xl-2 d-flex">
                      <h5>{{cartItems.quantity}}</h5>
                    </div>
                    <div class="col-md-3 col-lg-2 col-xl-2 offset-lg-1">
                      <h6 class="mb-0">Rs {{cartItems.price}}</h6>
                    </div>

                    <div class="col-md-1 col-lg-1 col-xl-1 text-end">
                      <a href="#!" class="text-muted"><i class="fas fa-times"></i></a>
                    </div>
                  </div>

                  <hr class="my-4">


                  <div class="pt-5">
                    <h6 class="mb-0"><a href="#" class="text-body"><i
                          class="fas fa-long-arrow-alt-left me-2"></i>Back to shop</a></h6>
                  </div>
                </div>
              </div>
              <div class="col-lg-4 bg-grey">
                <div class="p-5">
                  <h3 class="fw-bold mb-5 mt-2 pt-1">Summary</h3>
                  <hr class="my-4">

                  <div class="d-flex justify-content-between mb-4">
                    <h5 class="text-uppercase">items {{TotalQuantity}} </h5>

                  </div>

                  <h5 class="text-uppercase mb-3">Shipping</h5>

                  <div class="mb-4 pb-2">
                    <select class="select">
                      <option value="1">Standard-Delivery- Rs5.00</option>
                      <option value="2">Two</option>
                      <option value="3">Three</option>
                      <option value="4">Four</option>
                    </select>
                  </div>

                  <h5 class="text-uppercase mb-3">Give code</h5>

                  <div class="mb-5">
                    <div class="form-outline">
                      <input type="text" id="form3Examplea2" class="form-control form-control-lg" placeholder="Enter coupen code" [(ngModel)]="mycode" />

                      <button type="button" class="btn btn-dark btn-block btn-lg"
                    data-mdb-ripple-color="dark" (click)="applyCode()">Apply</button>
                    </div>
                  </div>

                  <hr class="my-4">

                  <div class="d-flex justify-content-between mb-5">
                    <h5 class="text-uppercase">Total price</h5>
                    <div>
                      <h5>Rs {{TotalCost}}</h5>
                  </div>

                  </div>


                   <div class="mb-5">
                    <div class="form-outline">
                      <input type="text" id="form3Examplea2" class="form-control form-control-lg" placeholder="Your delivary address" [(ngModel)]="Address"/>

                    </div>
                  </div>
                  <button type="button" class="btn btn-dark btn-block btn-lg"
                    data-mdb-ripple-color="dark" (click)="orderPlaced()">Place Order</button>

                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</section>
