  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'UserDashboard';

  searchResponse:searchPost[] = []; 
  random_pdt:boolean = true;


  temperoary=[ ["img4.jpg","img5.jpg","img6.jpg"], ["img9.jpg","img8.jpg","img7.jpg"]];


  product_recieved($event: searchPost[]){
    this.ra
    this.searchResponse = $event;
    console.log("Hi");
    for(const items in this.searchResponse){
      console.log(this.searchResponse[items]);
    }
  }
}
