import { Component, EventEmitter, OnInit, Output } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import {  FormControl, FormGroup, Validators } from '@angular/forms';
import { map } from 'rxjs';
import { searchPost } from './Search.model';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent implements OnInit{

  searchForm!: FormGroup;
  recieved_product:any[]=[];



  @Output() recievedOutput = new EventEmitter<any>();

  constructor(private http:HttpClient) {

  }

  ngOnInit() {
    this.searchForm = new FormGroup({
      'searchQuery':new FormControl(null, Validators.required),
    });
  };


  onSubmit(){
    console.log(this.searchForm.value);
    // let my_query = this.searchForm.value;
    this.http.post< {[key:string] :searchPost}>(
      // 'https://vish-620cc-default-rtdb.firebaseio.com/posts.json', this.searchForm.value

      'https://localhost:7151/Inventory/SearchProduct', this.searchForm.value,
    ).pipe( map(responseData => {
      const postArray:searchPost[] =[];
      for (const key in responseData){
        if(responseData.hasOwnProperty(key)){
          postArray.push({...responseData[key],searchQuery:key});
        }
      }
      return postArray;

    })
      )

    .subscribe(response=>{
      for(const items of response){
        console.log(items);

        this.recieved_product.push(items);


      }

      for(const item in this.recieved_product){

        console.log(this.recieved_product[item]);
      }

     this.recievedOutput.emit(this.recieved_product);

    });


  }



}
