import { Component, Input, OnInit } from '@angular/core';
import { searchPost } from '../header/Search.model';
import { EcommServiceService } from '../ecomm-service.service';

@Component({
  selector: 'app-search-result',
  templateUrl: './search-result.component.html',
  styleUrls: ['./search-result.component.css']
})
export class SearchResultComponent  {

  // @Input() searchResponse!:searchPost[];
  img_lis:string[]=[];
  Product_details:searchPost[]=[];
  fetchedPdt:boolean=false;
  constructor(private my_service:EcommServiceService){
    this.img_lis=my_service.temperoary;


  }

  ngOnInit(){
    for(let items of this.my_service.recieved_product ){
      console.log(items);

    }
    this.fetchedPdt=true;
    this.Product_details = this.my_service.recieved_product;
  }



}
<app-header></app-header>
<app-advertisement></app-advertisement>

<div class="container-fluid pdt-disp">

    <div *ngFor="let items of img_lis"  class="row float-container">
        <div class="col ">
            <div class="float-child">
                <img src="../../assets/images/{{items}}" class="img-dimention">

            </div>
                <div *ngIf="fetchedPdt==true" class="float-child" >
                    <h3>{{Product_details[0].name}}</h3>
                    <h4>{{Product_details[0].category}}</h4>
                    <p> {{Product_details[0].description}}</p>
                </div>
          </div>


      </div>


</div>
